import React from 'react'

import Card from '../UI/Card'
import Button from '../UI/Button'
import classes from './AddUser.module.css'

const AddUsers = () => {

  const [enteredUsername, setEnteredUsername] = React.useState('')
  const [enteredAge, setEnteredAge] = React.useState('')

  const addUserHandler = (e) => {
    e.preventDefault()

    if(enteredUsername.trim().length === 0 || enteredAge > 0) {
      setEnteredUsername('')
      setEnteredAge('')
      console.log(enteredUsername, enteredAge)
    }
  }

  const usernameChangeHandler = (event) => {
    setEnteredUsername(event.target.value)
  }

  const ageChangeHandler = (event) => {
    setEnteredAge(event.target.value)
  }

  return (
    <Card className={classes.input} >
      <form onSubmit={addUserHandler}>

      <label htmlFor='username'>Username</label>
      <input id='username' value={enteredUsername} tyoe='text' onChange={usernameChangeHandler} />

      <label htmlFor='age'>Age</label>
      <input id='age' tyoe='number' value={enteredAge} onChange={ageChangeHandler} />

      <Button type='submit'>Add User</Button>

      </form>
    </Card>
  )
}

export default AddUsers
